import React, { useState, useEffect } from "react";
import { Helmet } from 'react-helmet';
import HomeListingCard from '../components/HomeListingCard';
import { HomeStyles } from './Home.styled';
import  listings  from '../data/listings';
import companyLogo from '../image/logo.png';

const listingsPerPage = 3;
const arrayForHoldingListings = [];

const Home = () => {


  const [listingsToShow, setListingsToShow] = useState([]);
  const [count, setCount] = useState(1);

  const loopThroughListings = (count) => {
    for (
      let i = count * listingsPerPage - listingsPerPage;
      i < listingsPerPage * count;
      i++
    ) {
      if (listings[i] !== undefined) {
        arrayForHoldingListings.push(listings[i])
      }
    }
    setListingsToShow(arrayForHoldingListings);
  };

  const handleShowMorePosts = () => {
    console.log('did')
  };



  useEffect(() => {
    setCount((prevCount) => prevCount +1);
    loopThroughListings(count);
  }, []);

  return (
      <HomeStyles>

        <Helmet>
          <title>Pubweb Code Challenge</title>
          <meta name="description" content="Pubweb Code Challenge" />
        </Helmet>

        <header><img src={companyLogo} alt="Clayton Logo" /></header>

        <ul className="listings">
          {listings.slice(0, 9).map((data, key) => {
            return (
              <li key={key} className="listing">
                <HomeListingCard 
                  key={key}
                  imageURL={data.imageURL}
                  homeName={data.homeName}
                  startingPrice={data.startingPrice}
                  beds={data.beds}
                  baths={data.baths}
                  sqft={data.sqft}
                  isMultiSection={data.isMultiSection}
                />
              </li>
            );
          })}
        </ul>

        <button onClick={handleShowMorePosts}>Load More</button>

      </HomeStyles>
  )
};

export default Home;
