import React, { useState, useEffect, useRef } from 'react';
import { Helmet } from 'react-helmet';
import HomeListingCard from '../components/HomeListingCard';
import { HomeStyles } from './Home.styled';
import  listings  from '../data/listings';
import companyLogo from '../image/logo.png';

const Home = () => {
  //let arrayForHoldingHomes = [];
  //const [homesToShow, setHomesToShow] = useState([]),
  //  homesPerPage = 3,
  //  ref = useRef(homesPerPage),
  //  loopWithSlice = (start, end) => {
  //    const slicedHomes = listings.slice(start, end)
  //    arrayForHoldingHomes = arrayForHoldingHomes.concat(slicedHomes)
  //    setHomesToShow(arrayForHoldingHomes)
  //  },
  //  handleShowMoreHomes = () => {
  //    loopWithSlice(ref.current, ref.current + homesPerPage)
  //    ref.current += homesPerPage
  //  }
  //;
  //useEffect(() => {
  //  loopWithSlice(0, homesPerPage)
  //}, [])
  //let homesArray = [];
  //const initialHomes = listings.slice(0, 9);

  const [count, setCount] = useState(9);
  const showCount = listings.slice(0, {count})
  //const [count, setCount] = useState([]);
  //const countPerPage = 9;
  //let countArray = [];

  console.log(count);

  return (
      <HomeStyles>

        <Helmet>
          <title>Pubweb Code Challenge</title>
          <meta name="description" content="Pubweb Code Challenge" />
        </Helmet>

        <header><img src={companyLogo} alt="Clayton Logo" /></header>

        <ul className="listings">
          {listings.slice(0, {count}).map((data, key) => {
            return (
              <li key={key} className="listing">
                <HomeListingCard 
                  key={key}
                  imageURL={data.imageURL}
                  homeName={data.homeName}
                  startingPrice={data.startingPrice}
                  beds={data.beds}
                  baths={data.baths}
                  sqft={data.sqft}
                  isMultiSection={data.isMultiSection}
                />
              </li>
            );
          })}
        </ul>
        <button onClick={() => setCount(prevCount => prevCount + 3)}>Load more</button>
      </HomeStyles>
  )
};

export default Home;
