import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import HomeListingCard from '../components/HomeListingCard';
import { HomeStyles } from './Home.styled';
import  listings  from '../data/listings';
import companyLogo from '../image/logo.png';

const Home = () => {

  let loadMoreBtn = <React.Fragment><button onClick={() => setCount(prevCount => prevCount + 3)}>Load more</button></React.Fragment>,
  listed = listings,
  list = listings,
  isSorted = 'price';
  const
    [count, setCount] = useState(9),
    n = listings.length,
    [price, setPrice] = useState(listed),
    [sortDirection, setSortDirection] = useState('asc'),

    sortByPrice = () => {
      console.log('sort');
      const sorted = [...price].sort((a, b) => {
        return isSorted = a.startingPrice - b.startingPrice || a.homeName.localeCompare;
      })
      setPrice(sorted);
    },
    sortAscending = () => {
      console.log('asc');
      const sorted = [...price].sort((a, b) => {
        return a.startingPrice - b.startingPrice || a.homeName.localeCompare(b.homeName);
      })
      setPrice(sorted);
      setSortDirection('des');
    },
    sortDescending = () => {
      console.log('des');
      const sorted = [...list].sort((a, b) => {
        return b.startingPrice - a.startingPrice || a.homeName.localeCompare(b.homeName);
      })
      setPrice(sorted);
      setSortDirection('asc');
    },
    handleSort = (e) => {
      console.log(e.target);
      const dir = e.target.getAttribute('data-sortDirection');
      if (dir == 'des') {
        isSorted = 'asc';
        return sortAscending
      } else {
        isSorted = 'des';
        return sortDescending
      }
    };
  ;

  if (count > n) {
    loadMoreBtn = '';
  };

  return (
    <HomeStyles>

      <Helmet>
        <title>Pubweb Code Challenge</title>
        <meta name="description" content="Pubweb Code Challenge" />
      </Helmet>

      <header><img src={companyLogo} alt="Clayton Logo" /></header>

      <div className="sort-bar">
        <div>{n} homes available</div>
        <div className="price-select">
          Sort by: <button data-sortDirection={sortDirection} onClick={handleSort}>{isSorted}:</button>
        </div>
      </div>

      <ul className="listings">
        {list.slice(0, count).map((data, key) => {
          return (
            <li key={key} className="listing">
              <HomeListingCard 
                key={key}
                imageURL={data.imageURL}
                homeName={data.homeName}
                startingPrice={data.startingPrice}
                beds={data.beds}
                baths={data.baths}
                sqft={data.sqft}
                isMultiSection={data.isMultiSection}
              />
            </li>
          );
        })}
      </ul>

      <div className="load-more">{loadMoreBtn}</div>

      <footer>Copyright 2020 Clayton Homes. All Rights Reserved.</footer>

    </HomeStyles>
  )
};

export default Home;
