export const HomeStyles = styled.div`

  *{box-sizing:border-box}
  h3 {color: blue}

  ul.listings {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    grid-gap: .5rem;
    padding-inline-start: 0;

    width: 100%;
    max-width: 1440px;
    margin: 0 auto;
    text-align: center;
    box-sizing:border-box
  }
  li.listing {
    cursor: pointer;
    list-style: none;
    text-align: center;
    border: 0;
    boxshadow
    margin: 1rem 1rem 0 1rem
  }
  li.listing > figure {
    max-width: 320px;
  }

  li.listing > figure > img {
    width: 100%
  }
`;
