import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import HomeListingCard from '../components/HomeListingCard';
import { HomeStyles } from './Home.styled';
import  listings  from '../data/listings';
import companyLogo from '../image/logo.png';

const Home = () => {

  let loadMoreBtn = <React.Fragment><button type="button" onClick={() => setCount(prevCount => prevCount + 3)}>Load more</button></React.Fragment>;
  //console.log(listed);
  const
    [count, setCount] = useState(9),
    [sortConfig, setSortConfig] = useState(null),
    n = listings.length;

    const sortedItems = React.useMemo(() => {
      let sortableItems = [...items];
      if (sortConfig !== null) {
        sortableItems.sort((a, b) => {
          if (a[sortConfig.key] < b[sortConfig.key]) {
            return sortConfig.direction === 'ascending' ? -1 : 1;
          }
          if (a[sortConfig.key] > b[sortConfig.key]) {
            return sortConfig.direction === 'ascending' ? 1 : -1;
          }
          return 0;
        });
      }
      return sortableItems;
    }, [items, sortConfig]);

    const homeList = (props) => {
      const {items, requestSort, sortConfig } = useSortableData(props.listings);
      const getClassNamesFor = (name) => {
        if (!sortConfig) {
          return;
        }
        return sortConfig.key === name ? sortConfig.direction : undefined;
      };
      return (
        <div className="sort-bar">
        <div>{n} homes available</div>
        <div className="price-select">
          Sort by: <button>Price</button>
        </div>
      </div>
      );
    }

  if (count > n) {
    loadMoreBtn = '';
  };

  return (
    <HomeStyles>

      <Helmet>
        <title>Pubweb Code Challenge</title>
        <meta name="description" content="Pubweb Code Challenge" />
      </Helmet>

      <header><img src={companyLogo} alt="Clayton Logo" /></header>

      <div className="sort-bar">
        <div>{n} homes available</div>
        <div className="price-select">
          Sort by: <button>Price</button>
        </div>
      </div>

      <ul className="listings">
        {listings.slice(0, count).map((data, key) => {
          return (
            <li key={key} className="listing">
              <HomeListingCard 
                key={key}
                imageURL={data.imageURL}
                homeName={data.homeName}
                startingPrice={data.startingPrice}
                beds={data.beds}
                baths={data.baths}
                sqft={data.sqft}
                isMultiSection={data.isMultiSection}
              />
            </li>
          );
        })}
      </ul>

      <div className="load-more">{loadMoreBtn}</div>

      <footer>Copyright 2020 Clayton Homes. All Rights Reserved.</footer>

    </HomeStyles>
  )
};

export default Home;
